import styled from "styled-components";

export const MainDash = styled.main`
height: 100%;
display: grid;
display: grid;
grid-template-columns: 1fr;
grid-template-rows: repeat(2, 1fr) 9fr;
grid-column-gap: 0px;
grid-row-gap: 10px;
color: #000;
font-size: 3rem;
`

export const NavbarArea = styled.div`
grid-area: 1 / 1 / 2 / 2;
background: green;
padding: 10px 0;
display: flex;
justify-content: center;
align-items: center;
`

export const SearchBarArea = styled.div`
grid-area: 2 / 1 / 3 / 2;
background: red;
display: flex;
flex-direction: column;
justify-content: center;
align-items: flex-start;
padding: 0 1vw;

`

export const ContentArea = styled.div`
grid-area: 3 / 1 / 4 / 2;
background: yellow;
overflow-y: scroll;
height:70%;
`
