import React from 'react'
import { Nav, 
         NavbarContainer, 
         MobileIcon, 
         NavLogo, 
         NavMenu, 
         NavItem, 
         NavLinks,
         NavItemBtn,
         NavBtnLink,
         } from './Navbar.elements'
import {Button} from '../../../GlobalStyles'         
import {AiOutlineLogout} from 'react-icons/ai'
import {HiOutlineUserCircle} from 'react-icons/hi'
import { useState, useEffect } from 'react'

const Navbar = () => {

  const [click, setClick] = useState(false)
  const [button, setButton] = useState(true)

  const handleClick = () => setClick(!click)
  const showButton = () => {
    if(window.innerWidth <= 960){
      setButton(false)
    }else{
      setButton(true)
    }
  }

  useEffect(() => {
    showButton()
  }, [])

  window.addEventListener('resize', showButton)

  return (
    <>
      <Nav>
          <h4 style={{color:'#200752'}}>Bonjour Mohamed !</h4>
          <NavMenu>
          <NavItem>
              <NavLinks to ='/'><FaRegUserCircle style={{width:'35px', height:'35px'}}/></NavLinks>
            </NavItem>
            <NavItem>
              <NavLinks to ='/'><HiOutlineUserCircle style={{width:'35px', height:'35px'}}/></NavLinks>
            </NavItem>
          </NavMenu>
      </Nav>
    </>
  )
}

export default Navbar

