import styled from "styled-components";

export const Services = styled.div`
color : #fff;
padding: 160px 0;
background: ${({lightBg}) => (lightBg ? '#E8E9F7' : '#200752')};
`


export const Title = styled.img`
padding-right: 0;
border: 0;
max-width: 100%;
vertical-align:middle;
display: inline-block;
max-height: 200px;
`
