import styled from "styled-components";

export const Card = styled.div`
max-width: 1200px;
height: 700;
margin: 100px auto;
display: grid;
grid-template-columns: 1fr 1fr;
justify-content: center;
align-items: center;
padding: 100px auto;
border-radius: 100px;

@media screen and (max-width: 768px){
    grid-template-columns: 1fr;
    padding:0 20px;
    width: 100vw;
    height: 90vh;
    margin: 0;
}
`

export const CardTitle = styled.h1`
color : #fff;
font-size: 2rem;
text-align: center;
font-weight: 700;
font-family: 'Raleway';

@media screen and (max-width: 870px){
    font-size: 1rem;
}

@media screen and (max-width: 570px){
    font-size: 1rem;
}
`

export const CardLeftColumn = styled.div`
height: 100%;
padding: 50px;
@media screen and (max-width: 768px){
    display: none;
}
`

export const CardRightColumn = styled.div`
display: flex;
align-items: center;
justify-content: center;
height: 100%;
padding: auto 20px;
background-image: linear-gradient(-45deg, #200752 0%, #5320BA 100%);
border-radius: 0 100px 0 100px;

@media screen and (max-width: 768px){
    padding:auto 50px;
}
`

export const ForgotPasswordImage = styled.img`
padding-right: 0;
border: 0;
max-width: 100%;
vertical-align: middle;
display: inline-block;
max-height: 500px;
`

export const ForgotPasswordForm = styled.form`
@media screen and (max-width: 768px){
    width: 100%;
}
`


export const RegisterButton = styled.button`
border-radius: 30px;
background : #07E909;
white-space: nowrap;
padding:${({big}) => (big ? '12px 64px' : '10px 20px')};
color: #fff;
font-size: ${({fontBig}) => (fontBig ? '20px' : '16px')};
outline: none;
border:none;
cursor: pointer;
margin-top: 10px;

&:hover{
    transition: all 0.3s ease-out;
    background: #200752;
    background : ${({primary}) => (primary ? '#BFF4F8' : '#200752')};
}

@media screen and (max-width: 960px) {
    width: 50%;
}
`
