import styled from "styled-components";
import {Link} from 'react-router-dom'

export const CardLink = styled(Link)`
color : #fff;
display: flex;
flex-direction: row;
text-decoration : none;
padding : 0 1rem;
cursor : pointer;

&.active {
    color : #200752;
}

@media screen and (max-width: 960px){
    text-align: center;
    padding: 2rem;
    width: 100%;
    display: table;

    &.active {
    color : #200752;
    transition: all 0.3s ease;
    }
}
`

export const CardTitle = styled.h2`
font-size: 1.2rem;
color:#fff;
font-family: 'Raleway';
font-weight: 600;
`
