export const logIn = () => {
    setLocalStorage('user', {nom:'Ziani', prenom:'Mohamed'})
    return {
        type : 'SIGN_IN'
    }
}

export const logOut = () => {
    removeLocalStorage('user');
    return {
        type : 'SIGN_OUT'
    }
}
