import styled from 'styled-components'
import {NavLink as Link} from 'react-router-dom'


export const Nav = styled.nav`
background : #fff;
height : 80px;
display : flex;
justify-content : space-between;
padding : 0.5rem calc((90vw - 1000px) / 2);
z-index : 10; 
`

export const NavLink = styled(Link)`
color : #0CDB0E;
display: flex;
font-weight: 700;
align-items : center;
height : 100%;
text-decoration : none;
padding : 0 1rem;
cursor : pointer;

&.active {
    color : #200752;
}
`

export const MobileIcon = styled.div`
display :  none;

@media screen and (max-width : 768px) {
    display : block;
    position : absolute;
    top : 0;
    right : 0;
    transform: translate(-100%, 75%);
    font-size: 1.8rem;
    cursor : pointer;
}
`


export const NavMenu = styled.div`
display: flex;
align-items: center;
margin-right: -24px;

@media screen and ( max-width : 960px) {
    display: flex;
    flex-direction: column;
    width: 100%;
    height: 90vh;
    position: absolute;
    top: 80px;
    left: ${({click}) => (click ? 0 : '-100%')};
}
`

export const NavBtn = styled.nav`
 display: flex;
 align-items: center;
 margin-right: 24px;

 @media screen and (max-width : 768px){
    display: none;
 }
`

export const NavBtnLink = styled(Link)`
border-radius: 10px;
background: #200752;
padding : 10px 22px;
color : #fff;
font-weight: 500;
border : none;
outline: none;
cursor: pointer;
transition: all 0.2s ease-in-out;
text-decoration: none;

&:hover{
    transition: all 0.2s ease-in-out;
    background: #fff;
    color : #0CDB0E;
    text-decoration: none;
}
`

export const NavBtnLink1 = styled(Link)`
border-radius: 10px;
border: 2px solid #13C314;
background: white;
padding : 8px 22px;
color : #13C314;
font-weight: 700;
