import React from 'react'
import {DashContentContaier, CardsContainer, TableContainer, CardWrapper} from '../styles/TableauDeBord.elements'
import Card from '../../../../components/Dashboard.components/Cards/Card'
import Table from '../../../../components/Dashboard.components/Table/Table'

//cards data
import {CardsData} from '../../../../components/Dashboard.components/Cards/Cards.data'

const TableauDeBord = () => {
  return (
    <>
     <DashContentContaier>
        <CardsContainer>
            {CardsData.map((card, id) => {
              return (
                <CardWrapper style={{background:card.color}} key={id}>
                  <Card
                    title={card.heading}
                    color={card.color}
                    path={card.path}
                    icon={card.icon}
                    numbre={card.number}
                  />
                </CardWrapper>
              );
            })}
        </CardsContainer>
        <TableContainer>
          <div style={{padding: '1% 1%'}}>
            <MaterialTable
                  style={{maxWidth: '100%', overflow: 'auto', borderRadius:'10px'}}   
                  columns={[
                    { title: 'Adı', field: 'name' },
                    { title: 'Soyadı', field: 'surname' },
                    { title: 'Doğum Yılı', field: 'birthYear', type: 'numeric' },
                    { title: 'Doğum Yeri', field: 'birthCity', lookup: { 34: 'İstanbul', 63: 'Şanlıurfa' } }
                  ]}
                  data={[{ name: 'Mehmet', surname: 'Baran', birthYear: 1987, birthCity: 63 },
                  { name: 'Mehmet', surname: 'Baran', birthYear: 2001, birthCity: 63 }
                ]}
                  title="Vos Demande D'assurances"
                  onRowClick={((evt, selectedRow) => setSelectedRow(selectedRow.tableData.id))}
                  options={{
                    sorting:true,
                    filtering: true,
                    headerStyle: {
                      backgroundColor: '#200752',
                      color: '#FFF'
                    },
                    rowStyle: rowData => ({
                      backgroundColor: (selectedRow === rowData.tableData.id) ? '#EEE' : '#FFF'
                    })

                  }}
                />
            </div>
       </TableContainer>
     </DashContentContaier>
    </>
  )
}

export default TableauDeBord
