import React, {useState, useEffect} from 'react';
import {useRouteMatch, Link} from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios'
import {Form, Modal, Spinner} from 'react-bootstrap'


//components
import Navbar from '../components/Navbar'

//icons
import {FaCloudShowersHeavy, FaGasPump, FaVolumeUp} from 'react-icons/fa'

//images
import default_img from '../assets/default_image.png'

//image vente budjet 
import pneu1_vente_budjet from '../assets/vente_budjet/CONTI_PREMIUM_CONTACT_6_Continental.png'
import pneu2_vente_budjet from '../assets/vente_budjet/CROSSCLIMATEHAXX_Michelin.png'
import pneu3_vente_budjet from '../assets/vente_budjet/DUELER_H_T_684_II_Bridgistone.png'
import pneu4_vente_budjet from '../assets/vente_budjet/PILOT_SPORT_4_Michelin.png'
import pneu5_vente_budjet from '../assets/vente_budjet/SCORPION_VERDE_ALL_SEASON_Pireli.png'

const Styles = styled.div`
    overflow-x:hidden;

/*------------details container---------------*/
.details-container{
    display:flex;
    flex-direction:row;
    justify-content:center;
    align-items:center;
    padding:5%;
    height:100vh;
    width:100vw;
}
/**________________________________________________ */
.image-container{
    display:flex;
    flex-direction:column;
    width:30%;
    height:100%;
    border:1px solid #777
}

.active-image-container{
    display:flex;
    flex-direction:row;
    width:100%;
    height:80%;
}

.details-icons{
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:center;
    width:20%;
    height:80%;
}

.icon{
    width:50px;
    height:50px;
    color:#999;
    margin:10%; 
    border:2px solid #999;
    padding:7%;
    border-radius:50%;
}

.image-pneu-container{
    width:80%;
    height:80%;
}

.image-pneu{
    height:100%;
}

.default-img{
    display:flex;
    justify-content: flex-end;
    position: relative;
    height:100%;
    width: 100%;
    color:#333;
}

.text {
    color: #000;
    font-size: 1.2em;
    font-weight:600;
}

.overlay {
    display:flex;
    justify-content:flex-end;
    align-items:center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    height: 100%;
    width: 100%;
    opacity: 0.7;
    transition: .5s ease;
    background-color: #fff;
    padding-right:10%;
}

.image-marque-container{
    width:80%;
    display:flex;
    justify-content: center;
    align-items:center;
}

.image-marque{
    width:100%;
    height:100%;
}

.images-slider{
    display:flex;
    flex-direction:row;
    width:100%;
    height:20%;
}

.image-pneu-slide{
    width:70%;
    height:100%;
}

.slide{
    width:33%;
    height:100%;
    background:#eee;
    border: 1px solid #777;
}

.active-slide{
    display:flex;
    justify-content:center;
    align-items:center;
    width:38%;
    height:100%;
    border: 4px solid red;
}

/**_____________________________________________ */
.infos-container{
    width:40%;
    height:100%;
    padding: 0 3%;
}

.pneu{
    font-size:1.5em;
    font-weight:500;
    color:#444;

}

.dimension{
    font-size:1em;
    font-weight:500;
    color:#777;
    margin-bottom:5%;
}

.caracteristique{
    display:flex;
    flex-direction:row;
}

.titre{
    color:red;
    font-size:1em;
    margin-right:2%;
}

.paragraphe{
    color:#777;
    font-size:1.2em;
}

.titre-points-fort{
    color:#777;
    margin-top:5%
}

p{
    padding:0;
    margin:0;
}

ul{
    padding:0;
    margin:0;
}
/*______________________________________________*/
.commande-container{
    display:flex;
    flex-direction:column;
    justify-content:flex-start;
    align-items:flex-start;
    width:20%;
    height:100%;
    background:#eee;
    padding:1%;
}

.quantite{
    display:flex;
    flex-direction:row;
    justify-content:flex-start;
    width:100%;
    margin-top:3%;
}

.centre-mentage{
    display:flex;
    flex-direction:column;
    justify-content:flex-start;
    width:100%;
    margin-top:3%;
} 

div.form-group{
    margin-bottom:0 !important;
}

.button-commande{
    border:none;
    border-radius: 5px;
    background:linear-gradient(90.84deg, #EF1A23 0.61%, #FB3C29 99.42%);
    color:white;
    font-size:1em;
    font-weight:400;
    width:80%;
    height:auto;
    margin:5% 0;
    padding:5% 8%;
}
/*------------fin details container-----------*/

.info-Modal{
    width:90%;
    height:80%;
}

/*------------similar products---------------*/
.similar-products{
    display:flex;
    flex-direction:row;
    justify-content:center;
    align-items:center;
}
/*------------fin similar products------------*/
`;
const FormStyle = styled.form`
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:center;

    .form-row-1{
        display:flex;
        flex-direction:row;
        justify-content:center;
        align-items:center;
        width:100%;
    }

    .form-input-1{
        width:45%;
        height:50px;
        margin: 1.5%;
        padding:2%;
    }

    .form-input-2{
        width:93%;
        height:50px;
        margin: 1%;
        padding:2%;
    }

    .form-row-2{
        display:flex;
        flex-direction:row;
        justify-content:flex-end;
        align-items:flex-end;
        width:100%;
        margin:5% 2% 0 0;
    }

    .form-button{
        padding:1.5% 5%;
        margin:2%;
        border-radius:5px;
        border:none;
    }
`;




function Detail(){
    let match = useRouteMatch();

    //making sure email is correct
    const [email, setEmail] = useState('')
    const [error, setError] = useState('')
    const [message, setMessage] = useState('')
    const [loading, setLoading] = useState(false)
    const EmailRegex=/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/

    const checkEmail = (e) =>{
        setEmail(e.target.value)
        setCommande({...commande, email : e.target.value})

        if(EmailRegex.test(email) === false){
            setError('veuillez entrer un email valide !')
        }else{
            setCommande({...commande, email : e.target.value})
            setError('')
            return true
        }
    }

    const submit = (e) => {
        setLoading(true)
        e.preventDefault();

        var nom = commande.nom_client
        var prenom = commande.prenom_client
        var email = commande.email
        var telephone = commande.telephone
        var pneu = commande.designation_pneu
        var prix_pneu = commande.prix_pneu
        var quantite = commande.quantite
        var wilaya = commande.wilaya
        var frais_livraison = commande.frais_livraison
        var centre_mentage = commande.centre_montage
        var totale = total


        axios.post(`${process.env.REACT_APP_API_URL}/ajouter/commande`, {
                   nom : nom,
                prenom : prenom,
                 email : email,
             telephone : telephone,
                  pneu : pneu,
             prix_pneu : prix_pneu,
              quantite : quantite,
                wilaya : wilaya,
       frais_livraison : frais_livraison,
        centre_mentage : centre_mentage,
                 total : total
        })
        .then(res => {
           console.log(res.data)
           if(res.data){
            setLoading(false)
            setMessage('Votre commande a été reçue')
           }
        })

        .catch(err => {
            console.log(err)
        })

    }



    //show/hide Modal
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [pneu, setPneu] = useState([])
    const [wilayas, setWilayas] = useState([])
    const [total, setTotal] = useState(0)






    //data management
