max-height: 500px;
transition: all 0.2s ease-in-out;

&:hover{
    transform: scale(1.02);
    transition: all 0.3s ease-in-out;
    cursor: pointer;
}
`

export const Logo = styled.div`
justify-self: flex-start;
cursor: pointer;
