import styled from "styled-components";
import {Link} from 'react-router-dom'


export const CardContainer = styled.div`
width: 100%;
height:100%;
display: flex;
flex-direction: row;

`

export const ImageWrapper = styled.div`
width:50%;
height: 100%;
display: flex;
justify-content: center;
align-items: center;
`

export const CradImage = styled.img`
width:150px;
height: 150px;
`

export const ContentWrapper = styled.div`
width:50%;
height: 100%;
padding : 10px 5px;
`

export const CardTitle = styled.h2`
font-size: 1.2rem;
color:#200752;
font-family: 'Raleway';
font-weight: 600;
`

export const CardDesc = styled.p`
font-size: 0.8rem;
color:#999;
font-family: 'Raleway';
`

export const CardBtn = styled.button`
padding : 5px 10px;
margin-top: 10px;
background: #07E909;
color: #fff;
border-radius: 10px;
font-size: 0.9rem;
border: none;
`


export const CardLink = styled(Link)`
color : #fff;
display: flex;
flex-direction: row;
text-decoration : none;
cursor : pointer;

&:hover{
    text-decoration-line: none;
}

`
